package com.example.praise;import com.example.praise.parent.PraiseView;import android.app.Activity;import android.graphics.Color;import android.os.Bundle;import android.view.View;import android.widget.ImageView;public class MainActivity  extends Activity{	  private int[] corlors = new int[]{			  Color.parseColor("#ff4444"),Color.parseColor("#ff4444"),Color.parseColor("#ff4444"),Color.parseColor("#f66467")			  	  };	  private PraiseView mPraiseView;	  	    @Override	    protected void onCreate(Bundle savedInstanceState) {	        super.onCreate(savedInstanceState);	        setContentView(R.layout.activity_main);	        mPraiseView = new PraiseView(this);	    }	    public void good(View view) {	        ((ImageView) view).setImageResource(R.drawable.good_checked);	        mPraiseView.setSrc("+1", corlors[0], 12,null);	        mPraiseView.show(view);	    }	    public void image(View view) {	        ((ImageView) view).setImageResource(R.drawable.good_checked);	        mPraiseView.setSrc("", 0, 0, getResources().getDrawable(R.drawable.good_checked));	        mPraiseView.show(view);	    }}